							layer.close(index);
						
							var tpl = &#039;&lt;div style=&quot;margin-bottom: 10px;padding: 10px 0;&quot; id=&quot;dr_thumb_files_row&quot; class=&quot;files_row&quot;&gt;&lt;div&gt;&lt;span class=&quot;files_row_preview preview&quot;&gt;{preview}&lt;/span&gt;&lt;/div&gt;&lt;input type=&quot;hidden&quot;  id=&quot;dr_thumb&quot; class=&quot;files_row_id&quot; name=&quot;data[thumb]&quot; value=&quot;{id}&quot; /&gt;&lt;/div&gt;&#039;;
							var html = &quot;&quot;;
							var v = json.data.result[0];
							tpl = tpl.replace(/\{preview\}/g, v.preview);
							tpl = tpl.replace(/\{id\}/g, v.id);
							tpl = tpl.replace(/\{filepath\}/g, v.file);
							tpl = tpl.replace(/\{title\}/g, v.name);
							tpl = tpl.replace(/\{upload\}/g, v.upload);
							html+= tpl;
							$(&#039;#fileupload_thumb_files&#039;).html(html);
                            $(&#039;#fileupload_thumb&#039;).find(&#039;.fileinput-delete&#039;).show();
         					fileupload_thumb_edit();
							dr_tips(1, json.msg);
						} else {
							dr_tips(0, json.msg);
	
						}
						return false;
					}
