            success: function(layero, index){
                // 主要用于权限验证
                var body = layer.getChildFrame(&#039;body&#039;, index);
                var json = $(body).html();
                if (json.indexOf(&#039;&quot;code&quot;:0&#039;) &gt; 0 &amp;&amp; json.length &lt; 150){
                    var obj = JSON.parse(json);
                    layer.close(index);
                    dr_tips(0, obj.msg);
                }
                if (json.indexOf(&#039;&quot;code&quot;:1&#039;) &gt; 0 &amp;&amp; json.length &lt; 150){
                    var obj = JSON.parse(json);
                    layer.close(index);
                    dr_tips(1, obj.msg);
                }
            },
			content: url+&#039;&amp;is_ajax=1&#039;
		});
	});
	
     // 输入地址
