		var url = &quot;/index.php?s=api&amp;c=file&amp;m=input_file_url&amp;siteid=1&amp;p=b255345998f89b77f474f1ce7c9a9552&amp;fid=14&amp;one=1&quot;;
		layer.open({
			type: 2,
			title: &#039;&lt;i class=&quot;fa fa-edit&quot;&gt;&lt;/i&gt; 输入文件地址&#039;,
            fix:true,
            scrollbar: false,
            shadeClose: true,
