							$(&#039;#fileupload_thumb_files&#039;).html(tpl);
         					fileupload_thumb_edit();
                            $(&#039;#fileupload_thumb&#039;).find(&#039;.fileinput-delete&#039;).show();
							dr_tips(1, json.msg);
