// 修改组件
function fileupload_thumb_edit() {
	$(&#039;#fileupload_thumb_files .file_edit&#039;).fileupload({
		disableImageResize: false,
		autoUpload: true,
		maxFileSize: 10485760,
		acceptFileTypes: /(\.|\/)(jpg|gif|png)$/i,
		url: &#039;/index.php?s=api&amp;c=file&amp;siteid=1&amp;m=upload&amp;p=b255345998f89b77f474f1ce7c9a9552&amp;fid=14&#039;,
		dataType: &#039;json&#039;,
		progressall: function (e, data) {
			// 上传进度条 all
			var progress = parseInt(data.loaded / data.total * 100, 10);
			$(&quot;#fileupload_thumb .fileupload-progress&quot;).show();
			$(&quot;#fileupload_thumb .fileupload-progress&quot;).removeClass(&quot;fade&quot;);
			$(&quot;#fileupload_thumb .progress-bar-success&quot;).attr(&quot;style&quot;, &quot;width: &quot;+progress+&quot;%&quot;);
		},
		done: function (e, data) {
         
			$(&quot;#fileupload_thumb .fileupload-progress&quot;).hide();
			$(&quot;#fileupload_thumb .fileupload-progress&quot;).addClass(&quot;fade&quot;);
            dr_tips(data.result.code, data.result.msg);
            if (data.result.code == 0) {
            	return false;
            }
         
        	$(this).parents(&quot;.files_row&quot;).find(&quot;.files_row_id&quot;).val(data.result.id);
        	$(this).parents(&quot;.files_row&quot;).find(&quot;.files_row_name&quot;).val(data.result.info.file);
        	$(this).parents(&quot;.files_row&quot;).find(&quot;.files_row_preview&quot;).html(data.result.info.preview);
         
        },
		fail: function (e, data) {
			//console.log(data.errorThrown);
			dr_tips(0, &quot;系统故障: &quot;+data.errorThrown);
			$(&quot;#fileupload_thumb .fileupload-progress&quot;).addClass(&quot;fade&quot;);
			$(&quot;#fileupload_thumb .fileupload-progress&quot;).hide();
		},
	});
}
// 修改URL
function dr_file_edit_thumb(e) {
	var obj = $(e).parents(&quot;.files_row&quot;);
    var file = $(&quot;#dr_thumb&quot;).val();
	if ($(e).html().length &gt; 50) {
		// 本身是文件时跳过
		return;
	}
	var url = &quot;/index.php?s=api&amp;c=file&amp;m=input_file_url&amp;one=1&amp;siteid=1&amp;p=b255345998f89b77f474f1ce7c9a9552&amp;fid=14&amp;file=&quot;+file;
		layer.open({
			type: 2,
			title: &#039;&lt;i class=&quot;fa fa-edit&quot;&gt;&lt;/i&gt; 修改文件地址&#039;,
			shadeClose: true,
			shade: 0,
			area: [&quot;70%&quot;, &quot;60%&quot;],
			btn: [&quot;Ok&quot;],
			yes: function(index, layero){
				var body = layer.getChildFrame(&#039;body&#039;, index);
				 // 延迟加载
				var loading = layer.load(2, {
					time: 10000000
				});
				$.ajax({type: &quot;POST&quot;,dataType:&quot;json&quot;, url: url, data: $(body).find(&#039;#myform&#039;).serialize(),
					success: function(json) {
						layer.close(loading);
						if (json.code == 1) {
							layer.close(index);
							var tpl = &#039;&lt;div style=&quot;margin-bottom: 10px;padding: 10px 0;&quot; id=&quot;dr_thumb_files_row&quot; class=&quot;files_row&quot;&gt;&lt;div&gt;&lt;span class=&quot;files_row_preview preview&quot;&gt;{preview}&lt;/span&gt;&lt;/div&gt;&lt;input type=&quot;hidden&quot;  id=&quot;dr_thumb&quot; class=&quot;files_row_id&quot; name=&quot;data[thumb]&quot; value=&quot;{id}&quot; /&gt;&lt;/div&gt;&#039;;
							tpl = tpl.replace(/\{preview\}/g, json.data.preview);
							tpl = tpl.replace(/\{id\}/g, json.data.id);
							tpl = tpl.replace(/\{filepath\}/g, json.data.file);
							tpl = tpl.replace(/\{title\}/g, json.data.name);
							tpl = tpl.replace(/\{upload\}/g, json.data.upload);
							obj.remove();
							$(&#039;#fileupload_thumb_files&#039;).append(tpl);
                            $(&#039;#fileupload_thumb&#039;).find(&#039;.fileinput-delete&#039;).show();
         					fileupload_thumb_edit();
							dr_tips(1, json.msg);
						} else {
							dr_tips(0, json.msg);
	
						}
						return false;
					}
				});
				return false;
			},
            success: function(layero, index){
                // 主要用于权限验证
                var body = layer.getChildFrame(&#039;body&#039;, index);
                var json = $(body).html();
                if (json.indexOf(&#039;&quot;code&quot;:0&#039;) &gt; 0 &amp;&amp; json.length &lt; 150){
                    var obj = JSON.parse(json);
                    layer.close(index);
                    dr_tips(0, obj.msg);
                }
                if (json.indexOf(&#039;&quot;code&quot;:1&#039;) &gt; 0 &amp;&amp; json.length &lt; 150){
                    var obj = JSON.parse(json);
                    layer.close(index);
                    dr_tips(1, obj.msg);
                }
            },
			content: url+&#039;&amp;is_ajax=1&#039;
		});
}

function dr_file_remove_thumb() {
  $(&#039;#fileupload_thumb_files&#039;).html(&quot;&quot;);
  $(&#039;#fileupload_thumb&#039;).find(&#039;.fileinput-delete&#039;).hide();
}

