// 修改组件
function fileupload_file_edit() {
	$(&#039;#fileupload_file_files .file_edit&#039;).fileupload({
		disableImageResize: false,
		autoUpload: true,
		maxFileSize: 6291456000,
		acceptFileTypes: /(\.|\/)(rar|zip|exe|msi|7z)$/i,
		url: &#039;/index.php?s=api&amp;c=file&amp;siteid=1&amp;m=upload&amp;p=4ede498643d779624698df79c50a95c9&amp;fid=22&#039;,
		dataType: &#039;json&#039;,
		progressall: function (e, data) {
			// 上传进度条 all
			var progress = parseInt(data.loaded / data.total * 100, 10);
			$(&quot;#fileupload_file .fileupload-progress&quot;).show();
			$(&quot;#fileupload_file .fileupload-progress&quot;).removeClass(&quot;fade&quot;);
			$(&quot;#fileupload_file .progress-bar-success&quot;).attr(&quot;style&quot;, &quot;width: &quot;+progress+&quot;%&quot;);
		},
		done: function (e, data) {
         
			$(&quot;#fileupload_file .fileupload-progress&quot;).hide();
			$(&quot;#fileupload_file .fileupload-progress&quot;).addClass(&quot;fade&quot;);
            dr_tips(data.result.code, data.result.msg);
            if (data.result.code == 0) {
            	return false;
            }
         
        	$(this).parents(&quot;.files_row&quot;).find(&quot;.files_row_id&quot;).val(data.result.id);
        	$(this).parents(&quot;.files_row&quot;).find(&quot;.files_row_name&quot;).val(data.result.info.file);
        	$(this).parents(&quot;.files_row&quot;).find(&quot;.files_row_preview&quot;).html(data.result.info.preview);
         
        },
		fail: function (e, data) {
			//console.log(data.errorThrown);
			dr_tips(0, &quot;系统故障: &quot;+data.errorThrown);
			$(&quot;#fileupload_file .fileupload-progress&quot;).addClass(&quot;fade&quot;);
			$(&quot;#fileupload_file .fileupload-progress&quot;).hide();
		},
	});
}
