function dr_file_edit_file(e) {
	var obj = $(e).parents(&quot;.files_row&quot;);
    var file = $(&quot;#dr_file&quot;).val();
	if ($(e).html().length &gt; 50) {
		// 本身是文件时跳过
		return;
	}
	var url = &quot;/index.php?s=api&amp;c=file&amp;m=input_file_url&amp;one=1&amp;siteid=1&amp;p=4ede498643d779624698df79c50a95c9&amp;fid=22&amp;file=&quot;+file;
