							layer.close(index);
							var tpl = &#039;&lt;div style=&quot;margin-bottom: 10px;padding: 10px 0;&quot; id=&quot;dr_file_files_row&quot; class=&quot;files_row&quot;&gt;&lt;div&gt;&lt;span class=&quot;files_row_preview preview&quot;&gt;{preview}&lt;/span&gt;&lt;/div&gt;&lt;input type=&quot;hidden&quot;  id=&quot;dr_file&quot; class=&quot;files_row_id&quot; name=&quot;data[file]&quot; value=&quot;{id}&quot; /&gt;&lt;/div&gt;&#039;;
							tpl = tpl.replace(/\{preview\}/g, json.data.preview);
