							tpl = tpl.replace(/\{title\}/g, json.data.name);
							tpl = tpl.replace(/\{upload\}/g, json.data.upload);
							obj.remove();
							$(&#039;#fileupload_file_files&#039;).append(tpl);
                            $(&#039;#fileupload_file&#039;).find(&#039;.fileinput-delete&#039;).show();
         					fileupload_file_edit();
							dr_tips(1, json.msg);
						} else {
							dr_tips(0, json.msg);
	
						}
						return false;
					}
				});
				return false;
			},
            success: function(layero, index){
                // 主要用于权限验证
                var body = layer.getChildFrame(&#039;body&#039;, index);
                var json = $(body).html();
                if (json.indexOf(&#039;&quot;code&quot;:0&#039;) &gt; 0 &amp;&amp; json.length &lt; 150){
                    var obj = JSON.parse(json);
                    layer.close(index);
                    dr_tips(0, obj.msg);
                }
                if (json.indexOf(&#039;&quot;code&quot;:1&#039;) &gt; 0 &amp;&amp; json.length &lt; 150){
                    var obj = JSON.parse(json);
                    layer.close(index);
                    dr_tips(1, obj.msg);
                }
            },
			content: url+&#039;&amp;is_ajax=1&#039;
		});
}

