function fileupload_install_file_edit() {
	$(&#039;#fileupload_install_file_files .file_edit&#039;).fileupload({
		disableImageResize: false,
		autoUpload: true,
		maxFileSize: 536870912,
		acceptFileTypes: /(\.|\/)(jpg|gif|png|exe|html|php|rar|zip)$/i,
		url: &#039;/index.php?s=api&amp;c=file&amp;siteid=1&amp;m=upload&amp;p=6b96853166b80332c5deead6a16f9953&amp;fid=27&#039;,
